<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <title>Your Email</title>
    @stack('links')

    <style>
      #content a{
        text-decoration: underline;
        color: #06F;
      }

      .form-container {
        display: flex;
        justify-content: center;
        align-items: center;
        margin-top: 20px;
        margin-bottom: 50px;
    }

    .confirm-button {
        padding: 10px 20px;
        background-color: #007bff; 
        color: white;
        border: none; 
        border-radius: 5px; 
        cursor: pointer;
        font-size: 16px; 
    }

    .confirm-button:hover {
        background-color: #0056b3;
    }

    .header-table {
      max-width: 600px;
      width: 100%;
      background-color: #fff;
      padding: 40px;
    }

    @media(max-width: 320px) {
      .header-table {
        padding: 20px;
      }

      .product-cell {
        widows: 60%;
      }

      .footer-cell {
        display: block;
        text-emphasis: center;
      }

      .footer-cell h3 {
        margin: 20px 0 !important;
      }

      .footer-cell .logo {
        margin: 0 !important;
      }
    }
    </style>
    @stack('styles')
</head>
